SELECT `feeds`.`name` as `name`, `url`, `license_link`, `license_text`, `licenses`.`tag` as `license_name`, `languages`.`name` as `language_name`, `active`, `feeds`.`color_background` as `color_background`, `feeds`.`color_foreground` as `color_foreground`, `filtered_keywords` FROM `feeds` LEFT OUTER JOIN `licenses` ON `licenses`.`id` = `feeds`.`license_id` LEFT OUTER JOIN `languages` ON `languages`.`id` = `feeds`.`language_id`;